class TrustedAgent {
  address = '0x123...';
  stake = 1000_USDC;

  async register() {
    // Lock stake in smart contract
    await registry.register({
      address: this.address,
      stake: this.stake,
      capabilities: ['translate', 'research']
    });
  }

  async completeTask(task: Task, payment: number) {
    // User locks payment in escrow
    const escrow = await Escrow.create({
      agent: this.address,
      amount: payment,
      verifier: 'oracle-xyz'
    });

    // Complete task
    const result = await this.doTask(task);

    // Submit for verification
    await escrow.submitResult(result);

    // If verified: payment released automatically
    // If rejected: payment refunded + possible slashing
  }

  getReputation() {
    // Anyone can verify
    return registry.getScore(this.address);
  }
}
```

